import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useTranslation } from 'react-i18next';
import { 
  Globe2, 
  MessageSquarePlus, 
  ScanLine, 
  Send, 
  X, 
  MessagesSquare, 
  ThumbsUp, 
  Languages, 
  Quote, 
  MapPin, 
  Camera, 
  Share2,
  QrCode,
  ArrowRight,
  Search,
  Sun,
  Moon
} from 'lucide-react';
import './i18n';

// Types
type Message = {
  id: string;
  text: string;
  author: string;
  timestamp: string;
  language: string;
  isReply?: boolean;
  parentId?: string;
  upvotes: number;
  avatar?: string;
  category?: string;
  isPinned?: boolean;
  isVerified?: boolean;
};

type UserProfile = {
  name: string;
  country: string;
  avatar: string;
  isVerified?: boolean;
  joinDate?: string;
  messageCount?: number;
};

// Mock data
const mockUsers: UserProfile[] = [
  { name: "Sarah", country: "US", avatar: "ðŸ‘©â€ðŸ¦°", isVerified: true, joinDate: "2024-01-15", messageCount: 42 },
  { name: "Carlos", country: "ES", avatar: "ðŸ‘¨â€ðŸ’¼", isVerified: true, joinDate: "2024-02-03", messageCount: 38 },
  { name: "Ming", country: "CN", avatar: "ðŸ‘©â€ðŸŽ“", isVerified: false, joinDate: "2024-01-28", messageCount: 25 },
  { name: "Priya", country: "IN", avatar: "ðŸ‘©â€âš•ï¸", isVerified: true, joinDate: "2024-02-10", messageCount: 31 },
  { name: "Olena", country: "UA", avatar: "ðŸ‘©â€ðŸ«", isVerified: false, joinDate: "2024-01-20", messageCount: 19 },
  { name: "Niran", country: "TH", avatar: "ðŸ‘¨â€ðŸŽ¨", isVerified: true, joinDate: "2024-02-05", messageCount: 47 },
];

const mockQuestions: Message[] = [
  {
    id: "1",
    text: "Where is the best place to take a group photo?",
    author: "Sarah (US)",
    timestamp: "2m ago",
    language: "English",
    upvotes: 12,
    avatar: "ðŸ‘©â€ðŸ¦°",
    category: "photos",
    isPinned: true,
    isVerified: true
  },
  {
    id: "2",
    text: "Family-friendly restroom near Harvard Yard?",
    author: "Ming (CN)",
    timestamp: "5m ago",
    language: "Chinese",
    upvotes: 15,
    avatar: "ðŸ‘©â€ðŸŽ“",
    category: "accessibility"
  },
  {
    id: "3",
    text: "Quick lunch nearby that students actually like",
    author: "Olena (UA)",
    timestamp: "7m ago",
    language: "Ukrainian",
    upvotes: 9,
    avatar: "ðŸ‘©â€ðŸ«",
    category: "food"
  },
  {
    id: "4",
    text: "Is there a visitor center with maps and information?",
    author: "Priya (IN)",
    timestamp: "1m ago",
    language: "Hindi",
    upvotes: 6,
    avatar: "ðŸ‘©â€âš•ï¸",
    category: "info",
    isVerified: true
  }
];

const mockAnswers: Message[] = [
  {
    id: "a1",
    text: "The steps of Widener Library at golden hour.",
    author: "Mina (KR)",
    timestamp: "12m ago",
    language: "Korean",
    upvotes: 22,
    avatar: "ðŸ‘©â€ðŸ’¼",
    parentId: "1",
    isVerified: true
  },
  {
    id: "a2",
    text: "Northwest corner of the Yard facing Massachusetts Hall.",
    author: "Leo (AR)",
    timestamp: "25m ago",
    language: "Spanish",
    upvotes: 11,
    avatar: "ðŸ‘¨â€ðŸ’¼",
    parentId: "1"
  },
  {
    id: "a3",
    text: "Smith Campus Center, 1st floor â€” clean and accessible.",
    author: "Aya (JP)",
    timestamp: "8m ago",
    language: "Japanese",
    upvotes: 31,
    avatar: "ðŸ‘©â€ðŸŽ“",
    parentId: "2",
    isVerified: true
  },
  {
    id: "a4",
    text: "Clover or Felipe's roof if weather is nice.",
    author: "Ravi (IN)",
    timestamp: "1h ago",
    language: "Hindi",
    upvotes: 19,
    avatar: "ðŸ‘¨â€ðŸŽ¨",
    parentId: "3"
  },
  {
    id: "a5",
    text: "Smith Campus Center has a great visitor info desk on the first floor!",
    author: "Carlos (ES)",
    timestamp: "just now",
    language: "Spanish",
    upvotes: 4,
    avatar: "ðŸ‘¨â€ðŸ’¼",
    parentId: "4",
    isVerified: true
  }
];

// Components
function Pill({ children, onClick, className = "" }: { children: React.ReactNode; onClick?: () => void; className?: string }) {
  return (
    <span 
      className={`text-xs rounded-full bg-crimson-100 px-3 py-1 border border-crimson-200 cursor-pointer hover:bg-crimson-200 transition-colors ${className}`}
      onClick={onClick}
    >
      {children}
    </span>
  );
}

function LanguageSelector({ 
  selectedLang, 
  onLangChange 
}: { 
  selectedLang: string; 
  onLangChange: (lang: string) => void; 
}) {
  const { i18n } = useTranslation();
  const languages = [
    { code: "en", name: "English", flag: "ðŸ‡ºðŸ‡¸" },
    { code: "es", name: "Spanish", flag: "ðŸ‡ªðŸ‡¸" },
    { code: "zh", name: "Chinese", flag: "ðŸ‡¨ðŸ‡³" },
    { code: "th", name: "Thai", flag: "ðŸ‡¹ðŸ‡­" },
    { code: "uk", name: "Ukrainian", flag: "ðŸ‡ºðŸ‡¦" },
    { code: "hi", name: "Hindi", flag: "ðŸ‡®ðŸ‡³" }
  ];

  const handleLanguageChange = (langCode: string) => {
    console.log('Changing language to:', langCode);
    i18n.changeLanguage(langCode);
    onLangChange(langCode);
  };

  return (
    <div className="flex items-center gap-2">
      <Globe2 className="size-4 text-crimson-600" />
      <select
        value={selectedLang}
        onChange={(e) => handleLanguageChange(e.target.value)}
        className="rounded-md border border-crimson-300 bg-white px-2 py-1 text-sm focus:outline-none focus:ring-2 focus:ring-crimson-500"
      >
        {languages.map((lang) => (
          <option key={lang.code} value={lang.code}>
            {lang.flag} {lang.name}
          </option>
        ))}
      </select>
    </div>
  );
}

function Header({ 
  selectedLang, 
  onLangChange, 
  onBackToLanding,
  onViewMap,
  onToggleDarkMode,
  isDarkMode
}: { 
  selectedLang: string; 
  onLangChange: (lang: string) => void;
  onBackToLanding: () => void;
  onViewMap?: () => void;
  onToggleDarkMode: () => void;
  isDarkMode: boolean;
}) {
  const { t } = useTranslation();

  return (
    <div className="sticky top-0 z-40 backdrop-blur bg-white/90 dark:bg-gray-900/90 border-b border-crimson-200 dark:border-gray-700">
      <div className="max-w-4xl mx-auto px-4 py-3 flex items-center gap-3">
        <button
          onClick={onBackToLanding}
          className="flex items-center gap-2 text-crimson-600 hover:text-crimson-800 dark:text-crimson-400 dark:hover:text-crimson-300 transition-colors"
        >
          <ArrowRight className="size-4 rotate-180" />
          <span className="text-sm">{t('backToLanding')}</span>
        </button>
        <div className="flex items-center gap-2">
          <MessagesSquare className="size-6 text-crimson-600 dark:text-crimson-400" />
          <div className="font-semibold text-gray-900 dark:text-white">{t('harvardTouristChat')}</div>
          <div className="text-xs text-gray-500 dark:text-gray-400 ml-2">
            ({selectedLang.toUpperCase()})
          </div>
        </div>
        <div className="ml-auto flex items-center gap-3">
          {onViewMap && (
            <button
              onClick={onViewMap}
              className="flex items-center gap-2 text-crimson-600 hover:text-crimson-800 dark:text-crimson-400 dark:hover:text-crimson-300 transition-colors"
            >
              <MapPin className="size-4" />
              <span className="text-sm">Map</span>
            </button>
          )}
          <LanguageSelector selectedLang={selectedLang} onLangChange={onLangChange} />
          <button
            onClick={onToggleDarkMode}
            className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-800 transition-colors"
          >
            {isDarkMode ? <Sun className="size-4" /> : <Moon className="size-4" />}
          </button>
        </div>
      </div>
    </div>
  );
}

// Interactive Map Component
function InteractiveMap({ 
  selectedLang, 
  onLangChange, 
  onBackToLanding, 
  onViewForum, 
  onToggleDarkMode, 
  isDarkMode 
}: { 
  selectedLang: string; 
  onLangChange: (lang: string) => void;
  onBackToLanding: () => void;
  onViewForum: () => void;
  onToggleDarkMode: () => void;
