import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Globe2, 
  MessageSquarePlus, 
  ScanLine, 
  Send, 
  Sparkles, 
  X, 
  MessagesSquare, 
  ThumbsUp, 
  Languages, 
  Quote, 
  MapPin, 
  Camera, 
  Share2,
  QrCode,
  ArrowRight,
  User,
  Clock
} from 'lucide-react';

// Mock translation helper ‚Äî fakes translation by wrapping text with a language tag
const translate = (text: string, lang: string) => {
  if (lang === "English") return text;
  const prefix: Record<string, string> = {
    Spanish: "[ES]",
    Chinese: "[‰∏≠Êñá]",
    Thai: "[TH]",
    Ukrainian: "[UK]",
    Hindi: "[HI]",
  };
  return `${prefix[lang] ?? `[${lang}]`} ${text}`;
};

// Types
type Message = {
  id: string;
  text: string;
  author: string;
  timestamp: string;
  language: string;
  isReply?: boolean;
  parentId?: string;
  upvotes: number;
  avatar?: string;
};

type User = {
  name: string;
  country: string;
  avatar: string;
};

// Mock data
const mockUsers: User[] = [
  { name: "Sarah", country: "US", avatar: "üë©‚Äçü¶∞" },
  { name: "Carlos", country: "ES", avatar: "üë®‚Äçüíº" },
  { name: "Ming", country: "CN", avatar: "üë©‚Äçüéì" },
  { name: "Priya", country: "IN", avatar: "üë©‚Äç‚öïÔ∏è" },
  { name: "Olena", country: "UA", avatar: "üë©‚Äçüè´" },
  { name: "Niran", country: "TH", avatar: "üë®‚Äçüé®" },
];

const mockMessages: Message[] = [
  {
    id: "1",
    text: "What's the best tour for today? Looking for something family-friendly.",
    author: "Sarah (US)",
    timestamp: "2m ago",
    language: "English",
    upvotes: 12,
    avatar: "üë©‚Äçü¶∞"
  },
  {
    id: "2",
    text: "The free student-led tours at 10am and 2pm are amazing! Very engaging for kids.",
    author: "Carlos (ES)",
    timestamp: "1m ago",
    language: "Spanish",
    isReply: true,
    parentId: "1",
    upvotes: 8,
    avatar: "üë®‚Äçüíº"
  },
  {
    id: "3",
    text: "Where can I find good food nearby? Budget-friendly options preferred.",
    author: "Ming (CN)",
    timestamp: "5m ago",
    language: "Chinese",
    upvotes: 15,
    avatar: "üë©‚Äçüéì"
  },
  {
    id: "4",
    text: "Clover Food Lab is great! Also try Felipe's Taqueria for quick bites.",
    author: "Priya (IN)",
    timestamp: "3m ago",
    language: "Hindi",
    isReply: true,
    parentId: "3",
    upvotes: 11,
    avatar: "üë©‚Äç‚öïÔ∏è"
  },
  {
    id: "5",
    text: "Best photo spots around Harvard Yard? Want to capture the classic Harvard look.",
    author: "Olena (UA)",
    timestamp: "7m ago",
    language: "Ukrainian",
    upvotes: 9,
    avatar: "üë©‚Äçüè´"
  },
  {
    id: "6",
    text: "Widener Library steps at golden hour! Also the John Harvard statue area.",
    author: "Niran (TH)",
    timestamp: "4m ago",
    language: "Thai",
    isReply: true,
    parentId: "5",
    upvotes: 13,
    avatar: "üë®‚Äçüé®"
  },
  {
    id: "7",
    text: "Is there a visitor center with maps and information?",
    author: "Sarah (US)",
    timestamp: "1m ago",
    language: "English",
    upvotes: 6,
    avatar: "üë©‚Äçü¶∞"
  },
  {
    id: "8",
    text: "Smith Campus Center has a great visitor info desk on the first floor!",
    author: "Carlos (ES)",
    timestamp: "just now",
    language: "Spanish",
    isReply: true,
    parentId: "7",
    upvotes: 4,
    avatar: "üë®‚Äçüíº"
  }
];

// Components
function Pill({ children }: { children: React.ReactNode }) {
  return (
    <span className="text-xs rounded-full bg-zinc-100 px-2 py-1 border border-zinc-200">
      {children}
    </span>
  );
}

function LanguageSelector({ 
  selectedLang, 
  onLangChange 
}: { 
  selectedLang: string; 
  onLangChange: (lang: string) => void; 
}) {
  const languages = ["English", "Spanish", "Chinese", "Thai", "Ukrainian", "Hindi"];

  return (
    <div className="flex items-center gap-2">
      <Globe2 className="size-4 text-zinc-600" />
      <select
        value={selectedLang}
        onChange={(e) => onLangChange(e.target.value)}
        className="rounded-md border border-zinc-300 bg-white px-2 py-1 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
      >
        {languages.map((lang) => (
          <option key={lang} value={lang}>
            {lang}
          </option>
        ))}
      </select>
    </div>
  );
}

function Header({ 
  selectedLang, 
  onLangChange, 
  onBackToLanding 
}: { 
  selectedLang: string; 
  onLangChange: (lang: string) => void;
  onBackToLanding: () => void;
}) {
  return (
    <div className="sticky top-0 z-40 backdrop-blur bg-white/90 border-b border-zinc-200">
      <div className="max-w-4xl mx-auto px-4 py-3 flex items-center gap-3">
        <button
          onClick={onBackToLanding}
