function ChatInterface({ 
  selectedLang, 
  onLangChange, 
  onBackToLanding 
}: { 
  selectedLang: string; 
  onLangChange: (lang: string) => void;
  onBackToLanding: () => void;
}) {
  const [messages, setMessages] = useState<Message[]>(mockMessages);
  const [newMessage, setNewMessage] = useState('');
  const [replyingTo, setReplyingTo] = useState<Message | null>(null);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = () => {
    if (!newMessage.trim()) return;

    const randomUser = mockUsers[Math.floor(Math.random() * mockUsers.length)];
    const newMsg: Message = {
      id: Date.now().toString(),
      text: newMessage,
      author: `You (${randomUser.country})`,
      timestamp: 'just now',
      language: selectedLang,
      upvotes: 0,
      avatar: randomUser.avatar,
      isReply: replyingTo ? true : false,
      parentId: replyingTo?.id
    };

    setMessages(prev => [...prev, newMsg]);
    setNewMessage('');
    setReplyingTo(null);
  };

  const handleUpvote = (id: string) => {
    setMessages(prev => 
      prev.map(msg => 
        msg.id === id 
          ? { ...msg, upvotes: msg.upvotes + 1 }
          : msg
      )
    );
  };

  const handleReply = (message: Message) => {
    setReplyingTo(message);
  };

  const groupedMessages = messages.reduce((acc, message) => {
    if (message.isReply && message.parentId) {
      const parent = acc.find(m => m.id === message.parentId);
      if (parent) {
        parent.replies = [...(parent.replies || []), message];
      }
    } else {
      acc.push({ ...message, replies: [] });
    }
    return acc;
  }, [] as (Message & { replies: Message[] })[]);

  return (
    <div className="min-h-screen bg-zinc-50">
      <Header 
        selectedLang={selectedLang} 
        onLangChange={onLangChange}
        onBackToLanding={onBackToLanding}
      />

      <div className="max-w-4xl mx-auto px-4 py-6">
        {/* Welcome Banner */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-2xl p-6 mb-6"
        >
          <div className="flex items-center gap-3 mb-2">
            <Sparkles className="size-6" />
            <h2 className="text-xl font-semibold">Welcome to Harvard Tourist Chat!</h2>
          </div>
          <p className="text-blue-100">
            Ask questions, share tips, and connect with fellow visitors. 
            All messages are automatically translated to your selected language.
          </p>
        </motion.div>

        {/* Messages Feed */}
        <div className="space-y-4 mb-6">
          <AnimatePresence>
            {groupedMessages.map((message) => (
              <div key={message.id}>
                <MessageBubble 
                  message={message} 
                  selectedLang={selectedLang}
                  onUpvote={handleUpvote}
                />
                {message.replies && message.replies.length > 0 && (
                  <div className="ml-8 space-y-2">
                    {message.replies.map((reply) => (
                      <MessageBubble 
                        key={reply.id} 
                        message={reply} 
                        selectedLang={selectedLang}
                        onUpvote={handleUpvote}
                      />
                    ))}
                  </div>
                )}
              </div>
            ))}
          </AnimatePresence>
          <div ref={messagesEndRef} />
        </div>

        {/* Reply Indicator */}
        {replyingTo && (
          <motion.div
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            className="bg-blue-50 border border-blue-200 rounded-xl p-3 mb-4"
          >
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-2">
                <Quote className="size-4 text-blue-600" />
                <span className="text-sm text-blue-800">
                  Replying to {replyingTo.author}
                </span>
              </div>
              <button
                onClick={() => setReplyingTo(null)}
                className="text-blue-600 hover:text-blue-800"
              >
                <X className="size-4" />
              </button>
            </div>
            <div className="text-sm text-blue-700 mt-1 truncate">
              {translate(replyingTo.text, selectedLang)}
            </div>
          </motion.div>
        )}

        {/* Message Input */}
        <div className="sticky bottom-0 bg-white border-t border-zinc-200 p-4 -mx-4">
          <div className="flex gap-3">
            <div className="flex-1">
              <textarea
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder={replyingTo ? "Write your reply..." : "Ask a question or share a tip..."}
                className="w-full rounded-xl border border-zinc-300 p-3 text-sm resize-none focus:outline-none focus:ring-2 focus:ring-blue-500"
                rows={2}
                onKeyDown={(e) => {
                  if (e.key === 'Enter' && !e.shiftKey) {
                    e.preventDefault();
                    handleSendMessage();
                  }
                }}
              />
            </div>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={handleSendMessage}
              disabled={!newMessage.trim()}
              className="bg-blue-600 text-white p-3 rounded-xl hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            >
              <Send className="size-5" />
            </motion.button>
          </div>
          <div className="text-xs text-zinc-500 mt-2 text-center">
            Press Enter to send ‚Ä¢ Shift+Enter for new line
          </div>
        </div>
      </div>
    </div>
  );
}

